@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css");

@font-face {
  font-family: Migra;
  src: url("https://mwobacdzcczmcevupefl.supabase.co/storage/v1/object/public/images/migra.ttf");
}

#page {
  font-family: Migra;
  color: var(--color);
  line-height: 1.6; 
  font-size: 1rem;
  background: var(--background);
}

.section-container {
  max-width: var(--max-width, 1000px);
  margin: 0 auto;
  padding: 3rem var(--padding, 1rem); 
}

.heading {
  font-size: 3rem;
  line-height: 1;
  font-weight: 700;
  margin: 0;
}

.button {
  color: white;
  background: var(--color-accent);
  border-radius: 5px;
  padding: 8px 20px;
  transition: var(--transition);

  &:hover {
    box-shadow: 0 0 10px 5px rgba(0, 0, 0, 0.1);
  } 

  &.inverted {
    background: transparent; 
    color: var(--color-accent); 
    border: 2px solid var(--color-accent);
  }
}

header {
  color: var(--color-white, white);
  position: relative;
  overflow: hidden;
  background: var(--color-background);

  &.simple {
    background: var(--color-background);
  }
}

img {
  position: absolute;
  inset: 0em;
  height: 100%;
  width: 100%;
  object-fit: cover;
}

.heading-group {
  background: #12121260; /* overlay */
  display: grid;
  place-items: center;
  padding-top: clamp(4rem,16vw,12rem);
  padding-bottom: clamp(4rem,16vw,12rem);  
  position: relative;
  gap: 1rem;

  .heading {
    font-size: clamp(2rem, 10vw, 3.5rem);
    color: var(--color-white, white);
  }

  .subheading {
    place-self: center;
    font-size: 1.125rem;
    line-height: 1.3;
    color: var(--color-inverted);
  }
}

.buttons {
  margin-top: 1rem;
  display: flex;
  justify-content: center;
  gap: 1rem;

  .button {
    background: transparent;
    color: var(--color-inverted);
    border: 1.5px solid white;

    &.disabled {
      display: none;
      border: 1.5px solid lightgrey;
      color: var(--color-disabled);
    }
  }

}

svg {
  width: 100%;
  background: #183b56a6;
  position: relative;
  fill: var(--background);
}

.section-container { /* Defined in Site CSS */
  display: grid;
  gap: 2rem;

  @media (min-width: 800px) {
    grid-template-columns: 3fr 2fr;
    gap: 14rem;
  }
}

.content {
  display: grid;
  place-content: flex-start;
  gap: 1rem;

  .body{
    margin-bottom: .5rem;
  }
  .social-links {
    display: grid;
    gap: 0.75rem;

    a {
      display: flex;
      align-items: center;
      gap: 0.75rem;

      .icon {
        font-size: 1.75rem;
        color: var(--color-accent, rebeccapurple);
        transition: var(--transition, 0.1s transform);
      }

      &:hover .icon {
        transform: scale(1.1);
      }
    }
  }
}

form {
  display: grid;
  gap: 1rem;
  padding: 2rem;
  border: 1px solid var(--border-color, #eee);
  border-radius: var(--border-radius);

  label {
    display: grid;
    gap: 0.25rem;

    span {
      font-size: 0.75rem;
      font-weight: 500;
    }

    input,
    textarea {
      font-size: 1rem;
      padding: 0.5rem;
      outline-color: transparent;
      transition: var(--transition, 0.1s outline-color);
      border: 1.5px solid var(--border-color, #eee);
      border-radius: 4px;

      &:focus {
        outline-color: var(--color-accent, rebeccapurple);
      }
    }
  }

  .button { /* Defined in Site CSS */
    margin-top: 1rem;
  }
}

