.body {
  max-width: 700px;
  margin-bottom: 2.25rem;
}

.buttons {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 1rem;

  .button {
    display: flex;
    align-items: center;
    gap: 0.25rem;
  }
}

.heading-group {
  margin-bottom: 6rem;
  text-align: center;
  place-items: center;
}

.teasers {
  display: grid;
  gap: 6rem;
}

.teaser {
  display: grid;
  gap: 3rem;

  @media (min-width: 1200px) {
    gap: 6rem;
  }

  img {
    width: 100%;
    border-radius: var(--border-radius);
  }

  .body {
    /* Defined in Site CSS */
    display: grid;
    place-items: flex-start;
    gap: 1rem;
    padding: 0;
    line-height: 1.5;
    max-width: 450px;

    .title {
      color: var(--color-heading);
      font-size: 2rem;
      font-family: var(--font-heading);
      font-weight: 500;
      line-height: 1.1;
    }

    a.link {
      display: flex;
      align-items: center;
      gap: 0.5rem;
    }
  }

  @media (min-width: 800px) {
    grid-template-columns: 1fr 1fr;
    place-items: center;

    /* switch direction for even items */
    &:nth-child(odd) img {
      grid-row: 1;
      grid-column: 2;
    }

    &:nth-child(even) img {
      grid-row: 1;
      grid-column: 1;
    }
  }
}
