:root{
  --color: #354658;
  --color-accent: #eee;
  --background: #eee;
  --color-background: #354658;
  --border-color: #354658;
  --secondary-color-background: #cacccf;
  --max-width: max(80%, 1000px);
}

#page {
  font-family: "Neue Montreal";
  color: var(--color);
  line-height: 1.6; 
  font-size: 1rem;
  background: var(--background);
}

.section-container {
  max-width: var(--max-width, 1000px);
  margin: 0 auto;
  padding: 3rem var(--padding, 1rem); 
}

.heading {
  font-family: Migra;
  font-size: clamp(1.2rem,3vw,4rem);
  line-height: 1;
  font-weight: 700;
  margin: 0;
}

.accent-background {
  background-color: var(--background);
}

.button {
  color: var(--color-accent);
  border-color: var(--color-background);
  background: var(--color-background);
  border-radius: 5px;
  padding: 8px min(20px, 2vw);
  transition: var(--transition);

  &:hover {
    box-shadow: 0 0 10px 5px rgba(0, 0, 0, 0.1);
  } 

  &.inverted {
    background: transparent; 
    color: var(--color-accent); 
    border: 2px solid var(--color-accent);
  }
}

header {
  color: var(--color-accent, white);
  position: relative;
  overflow: hidden;
  background: var(--color-background);

  &.simple {
    background: var(--color-background);
  }
}

img {
  position: absolute;
  inset: 0em;
  height: max(250px, 100%);
  width: 100%;
  object-fit: cover;
}

.heading-group {
  background: #12121250; /* overlay */
  display: grid;
  place-items: center;
  padding-top: min(40rem,50vw);
  padding-bottom: min(2rem, 4vw);
  position: relative;
  gap: min(1vw,10rem);

  .heading {
    font-size: clamp(1.75rem, 5vw, 10rem);
    color: var(--color-white, white);
  }

  .subheading {
    place-self: center;
    font-size: clamp(1.25rem, 3vw, 5rem);
    line-height: 1.3;
    color: var(--color-inverted);
  }
}

.buttons {
  margin-top: 1rem;
  display: flex;
  justify-content: center;
  gap: 1rem;

  .button {
    background: transparent;
    font-size: clamp(1rem, 2vw, 3rem);
    color: var(--color-inverted);
    border: 1.5px solid white;

    &.disabled {
      display: none;
      border: 1.5px solid lightgrey;
      color: var(--color-disabled);
    }
  }

}

svg {
  width: 100%;
  background: #183b56a6;
  position: relative;
  fill: var(--background);
}

.section-container {
  padding-top: 7rem;
} /* Defined in Site CSS */

.heading {
  margin-bottom: 3rem;
  text-align: center;
}

.items {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 5rem 3rem;
}

.item {
  display: grid;
  place-content: start;
  gap: 1rem;

  .title {
    color: var(--color-heading);
    font-size: 1.5rem;
    font-weight: 600;
  }
}

.left {
  .heading {
    text-align: left;
  }
}

.section-container { /* Defined in Site CSS */
  display: grid;
  justify-content: center;
}

.card {
  padding: 4rem clamp(2rem, 10vw, 7rem); 
  border: 1px solid var(--border-color, #eee);
  border-radius: var(--border-radius);
  text-align: center;
  display: grid;

}

.heading {
  margin-bottom: 1.5rem;
} /* Defined in Site CSS */

.body {
  max-width: 700px;
  margin-bottom: 2.25rem;
}

.buttons {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 1rem;

